import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Header from '../components/Header';
import Footer from '../components/Footer';

const AdminPage = () => {
  const [projects, setProjects] = useState([]);
  const [messages, setMessages] = useState([]);
  const [newProject, setNewProject] = useState({
    title: '',
    description: '',
    image: '',
    link: '',
  });

  useEffect(() => {
    axios.get('/api/projects').then((res) => setProjects(res.data));
    axios.get('/api/messages').then((res) => setMessages(res.data));
  }, []);

  const addProject = () => {
    axios
      .post('/api/projects', newProject)
      .then((res) => setProjects([...projects, res.data]))
      .catch((err) => console.error(err));
  };

  return (
    <>
      <Header />

      <main className="mt-5 pt-4">
        <div className="container py-5">
          <h1 className="text-center mb-4">Admin Dashboard</h1>

          <section className="mb-5">
            <h2 className="mb-3">Messages</h2>
            <ul className="list-group">
              {messages.map((msg) => (
                <li className="list-group-item" key={msg._id}>
                  <strong>{msg.name}:</strong> {msg.message} <em>({msg.email})</em>
                </li>
              ))}
            </ul>
          </section>

          <section>
            <h2 className="mb-3">Add New Project</h2>
            <form
              onSubmit={(e) => {
                e.preventDefault();
                addProject();
              }}
            >
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Title"
                  value={newProject.title}
                  onChange={(e) =>
                    setNewProject({ ...newProject, title: e.target.value })
                  }
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Description"
                  value={newProject.description}
                  onChange={(e) =>
                    setNewProject({ ...newProject, description: e.target.value })
                  }
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Image URL"
                  value={newProject.image}
                  onChange={(e) =>
                    setNewProject({ ...newProject, image: e.target.value })
                  }
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  className="form-control"
                  placeholder="Link"
                  value={newProject.link}
                  onChange={(e) =>
                    setNewProject({ ...newProject, link: e.target.value })
                  }
                />
              </div>
              <button type="submit" className="btn btn-primary">
                Add Project
              </button>
            </form>
          </section>
        </div>
      </main>

      <Footer />
    </>
  );
};

export default AdminPage;
