import React, { useEffect } from "react";
import "../components/css/styles.css";

const About = () => {
  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          if (entry.intersectionRatio >= 0.8) {
            // Remove the 'show' class from all elements
            document.querySelectorAll(".show").forEach((el) => el.classList.remove("show"));

            // Add the 'show' class to the currently intersecting element
            entry.target.classList.add("show");
          }
        });
      },
      { threshold: 0.8 } // Trigger when 80% of the element is visible
    );

    const hiddenElements = document.querySelectorAll(".hidden");
    hiddenElements.forEach((el) => observer.observe(el));

    // Cleanup observer on component unmount
    return () => observer.disconnect();
  }, []); // Empty dependency array ensures this runs only once

  return (
    <div id="about" className="about-section">
      {[
        { id: 1, title: "About Me", text: "I specialize in Web Development, crafting responsive applications with PHP, Laravel, JavaScript, NodeJS, HTML, CSS, and more. Skilled in server management, SQL, and deployment on Nginx/Apache, I also design interfaces using tools like Figma and Photoshop. With solid backend expertise and a passion for growth, I’m driven to deliver satisfaction and tackle challenges with a smile." },
        { id: 2, title: "Experience", text: "Back End Developer (TOLDIT Ltd., Jan 2023 – Present): Lead backend development using PHP Laravel, creating secure APIs for various applications, including a multi-tenant hospital management system, e-commerce platforms, and shipping solutions. Responsible for server optimization, database management, and delivering high-performance, scalable solutions through collaboration and technical leadership. Frontend Developer (University of Cape Coast, Oct 2021 – Sep 2022): Developed a teaching timetable scheduler web application to enhance scheduling efficiency for lecturers and students." },
        { id: 3, title: "Education", text: "University of Cape Coast Bachelor of Science - Computer Science" },
        { id: 4, title: "Skills and Tools", text: "Programming is the future, and you're a part of it." },
      ].map((section) => (
        <section key={section.id} className="hidden">
          <div className="content">
          <hr></hr>
            <h2>{section.title}</h2>
            <p>{section.text}</p>
          <hr></hr>
          </div>
        </section>
      ))}
    </div>
  );
};

export default About;
