import React, { useEffect, useState } from "react";
import axios from "axios";
import "../components/css/styles.css";

const Portfolio = () => {
  const [projects, setProjects] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProjects = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/projects");
        setProjects(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching projects:", error);
        setLoading(false);
      }
    };

    fetchProjects();
  }, []);

  if (loading) {
    return (
      <section className="portfolio-section py-5">
        <div className="container text-center">
          <h2>Loading Projects...</h2>
        </div>
      </section>
    );
  }

  return (
    <section id="portfolio" className="portfolio-section py-5">
      <div className="container">
        <h2 className="text-center mb-5">Project Portfolio</h2>
        <div className="row justify-content-center g-4">
          {projects.map((project) => (
            <div className="col-md-4 d-flex justify-content-center" key={project.id}>
              <div className="card h-100">
                <img
                  id="image"
                  src={`http://localhost:5000${project.image}`}
                  alt={project.title}
                  className="card-img-top"
                  style={{ height: "150px", objectFit: "cover" }}
                />
                <div className="card-body d-flex flex-column">
                  <h5 className="card-title">{project.title}</h5>
                  <p className="card-text">{project.description}</p>
                  <a
                    href={project.link}
                    className="btn mt-auto"
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    View Project
                  </a>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Portfolio;
