import React, { useEffect, useState } from "react";
import "../components/css/styles.css";

const About = () => {
  // const [inView, setInView] = useState(false);

  // // Detect visibility of the entire "About" section
  // useEffect(() => {
  //   const observer = new IntersectionObserver(
  //     (entries) => {
  //       entries.forEach((entry) => {
  //         if (entry.intersectionRatio >= 0.01) {
  //           setInView(true);  // The About section is at least 1% in view
  //         } else {
  //           setInView(false);  // The About section is less than 1% in view
  //         }
  //       });
  //     },
  //     { threshold: 0.01 } // 1% visibility trigger
  //   );

    // const aboutSection = document.getElementById("about");
    // observer.observe(aboutSection);

  //   return () => observer.disconnect();
  // }, []);

  const observer = new IntersectionObserver(
    (entries) => {
        entries.forEach((entry) => {
            if (entry.intersectionRatio >= 0.8) {
                // Remove the 'show' class from all elements
                document.querySelectorAll('.show').forEach((el) => el.classList.remove('show'));

                // Add the 'show' class to the currently intersecting element
                entry.target.classList.add('show');
            }
        });
    },
    {
        threshold: 0.8, // Trigger when 20% of the element is visible
    }
);

const hiddenElements = document.querySelectorAll('.hidden');
hiddenElements.forEach((el) => observer.observe(el));


  return (
    <div id="about" className={`about-section`}>
      <section >
    <div className="content, hidden">
        <h2>Section 1</h2>
        <p>Coding opens doors to infinite possibilities.</p>
    </div>
</section>

<section >
    <div className="content, hidden">
        <h2>Section 2</h2>
        <p>Discover the art of problem-solving through code.</p>
    </div>
</section>

<section >
    <div className="content, hidden">
        <h2>Section 3</h2>
        <p>Build, create, and inspire the world.</p>
    </div>
</section>

<section >
    <div className="content, hidden">
        <h2>Section 4</h2>
        <p>Programming is the future, and you're a part of it.</p>
    </div>
</section>
    </div>
  );
};

export default About;
