import React, { useEffect, useState } from "react";
import "../components/css/styles.css";

const About = () => {
  const [scrollY, setScrollY] = useState(0);

  const handleScroll = () => {
    setScrollY(window.scrollY);
  };

  useEffect(() => {
    window.addEventListener("scroll", handleScroll);
    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  const slideInStyle = {
    transform: `translateX(${Math.min(scrollY / 5, 100)}%)`, // Adjust sliding speed
    opacity: `${Math.min(scrollY / 500, 1)}`, // Fade-in effect as you scroll
  };

  return (
    <section id="about" className="about-section">
      <div className="about-container">
        {/* Slide 1: Introduction */}
        <div className="about-slide" style={slideInStyle}>
          <h2>About Me</h2>
          <p>
            Passionate Web Developer creating scalable and responsive solutions
            that improve user experience.
          </p>
        </div>

        {/* Slide 2: Skills */}
        <div className="about-slide" style={slideInStyle}>
          <h2 className="text-primary">Skills</h2>
          <ul>
            <li>PHP, Laravel, JavaScript</li>
            <li>HTML, CSS, Bootstrap</li>
            <li>Server Management, SQL</li>
          </ul>
        </div>

        {/* Slide 3: Experience */}
        <div className="about-slide" style={slideInStyle}>
          <h2 className="text-primary">Experience</h2>
          <p>
            <strong>TOLDIT Ltd.</strong> <br />
            Main Backend Developer – Building APIs and web applications.
          </p>
          <p>
            <strong>University of Cape Coast</strong> <br />
            Frontend Developer – Crafted user-friendly interfaces for web
            applications.
          </p>
        </div>

        {/* Slide 4: Education & Certifications */}
        <div className="about-slide" style={slideInStyle}>
          <h2 className="text-primary">Education</h2>
          <p>
            Bachelor of Science - Computer Science <br />
            University of Cape Coast
          </p>
          <h2 className="text-primary mt-4">Certifications</h2>
          <p>Responsive Web Design - freeCodeCamp</p>
        </div>
      </div>
    </section>
  );
};

export default About;
