import React, { useEffect, useState } from "react";
import "../components/css/styles.css";

const About = () => {
  const [activeComponent, setActiveComponent] = useState(0);

  useEffect(() => {
    const observer = new IntersectionObserver(
      (entries) => {
        entries.forEach((entry) => {
          const { id } = entry.target;
          if (entry.isIntersecting) {
            const index = parseInt(id.replace('section-', ''));
            setActiveComponent(index);
          }
        });
      },
      { threshold: 0.5 } // Trigger when 50% of the element is visible
    );

    const sections = document.querySelectorAll(".about-slide");
    sections.forEach((section) => observer.observe(section));

    return () => observer.disconnect();
  }, []);

  return (
    <section id="about" className="about-section">
      <div className="about-container">
        {/* Slide 1: Introduction */}
        <div
          className={`about-slide ${activeComponent === 0 ? "in-view" : ""}`}
          id="section-0"
        >
          <h2>About Me</h2>
          <p>
            Passionate Web Developer creating scalable and responsive solutions
            that improve user experience.
          </p>
        </div>

        {/* Slide 2: Skills */}
        <div
          className={`about-slide ${activeComponent === 1 ? "in-view" : ""}`}
          id="section-1"
        >
          <h2 className="text-primary">Skills</h2>
          <ul>
            <li>PHP, Laravel, JavaScript</li>
            <li>HTML, CSS, Bootstrap</li>
            <li>Server Management, SQL</li>
          </ul>
        </div>

        {/* Slide 3: Experience */}
        <div
          className={`about-slide ${activeComponent === 2 ? "in-view" : ""}`}
          id="section-2"
        >
          <h2 className="text-primary">Experience</h2>
          <p>
            <strong>TOLDIT Ltd.</strong> <br />
            Main Backend Developer – Building APIs and web applications.
          </p>
          <p>
            <strong>University of Cape Coast</strong> <br />
            Frontend Developer – Crafted user-friendly interfaces for web
            applications.
          </p>
        </div>

        {/* Slide 4: Education & Certifications */}
        <div
          className={`about-slide ${activeComponent === 3 ? "in-view" : ""}`}
          id="section-3"
        >
          <h2 className="text-primary">Education</h2>
          <p>
            Bachelor of Science - Computer Science <br />
            University of Cape Coast
          </p>
          <h2 className="text-primary mt-4">Certifications</h2>
          <p>Responsive Web Design - freeCodeCamp</p>
        </div>
      </div>
    </section>
  );
};

export default About;
